#include &lt;cmath&gt;
using namespace std;

const int N = 1e5 + 10;

int n, p;
int a[N], b[N];

bool ck(double sb) { // 用sb的时间
    double pp = p * sb; // 一共可以冲这么多电
    for (int i = 1; i &lt;= n; i++) {
        pp -= max(0.0, a[i] * sb - b[i]);    // 要消耗的电 - 本来的电 = 需要冲的电
        if (pp &lt; 0) return 0;
    }
    return 1;
}

int main() {
    cin &gt;&gt; n &gt;&gt; p;
    for (int i = 1; i &lt;= n; i++) scanf("%d%d", a + i, b + i);

    double l = 0, r = 1e12;

    while ( (r-l)&gt;1e-6 ) {
        double mid = (l + r) / 2;
        if (ck(mid)) l = mid;
        else r = mid;
    }

    if ( fabs(1e12-l) &lt;= 1e-6) cout &lt;&lt; -1;
    else cout &lt;&lt; l;

    return 0;
}
