                    dp[i][j][x][y] = max(dp[i][j][x][y], dp[i-1][j][x-1][y]+t ); 
                    dp[i][j][x][y] = max(dp[i][j][x][y], dp[i-1][j][x][y-1]+t );
                    dp[i][j][x][y] = max(dp[i][j][x][y], dp[i][j-1][x-1][y]+t );
                    dp[i][j][x][y] = max(dp[i][j][x][y], dp[i][j-1][x][y-1]+t );
                }

    cout&lt;&lt;dp[n][n][n][n];
    return 0;
}
