                int v      = g[i][k-i];
                if(i!=j) v+= g[j][k-j];

                dp[k][i][j] = max(dp[k][i][j], v + dp[k-1][i][j]);
                dp[k][i][j] = max(dp[k][i][j], v + dp[k-1][i][j-1]);
                dp[k][i][j] = max(dp[k][i][j], v + dp[k-1][i-1][j]);
                dp[k][i][j] = max(dp[k][i][j], v + dp[k-1][i-1][j-1]);
