C++<pre><code>#include &lt;iostream&gt;

class A{
public:
    A(int x, int y): _x(x), _y(y){};

    // 重载 输出运算符 &lt;&lt;
    friend std::ostream &amp;operator&lt;&lt;(std::ostream&amp; output, A a){
        output &lt;&lt; a._x &lt;&lt; " " &lt;&lt; a._y;
        return output;            
    }

    // 重载 输入运算符 &gt;&gt;
    friend std::istream &amp;operator&gt;&gt;(std::istream&amp; input, A a){
        input &gt;&gt; a._x &gt;&gt; a._y;
        return input;            
    }

    // 重载 一元运算符 -
    A operator- (){
        return A(-_x, -_y);
    }

    // 重载 二元运算符 +
    A operator+ (A a){
        return A(_x + a._x, _y + a._y);
    }

    // 重载 关系运算符 &lt;
    bool operator&lt; (A a){
        if(_x &lt; a._x) return true;
        if(_x &gt; a._x) return false;
        if(_y &lt; a._y) return true;
        return false;
    }

private:
    int _x, _y;
};

