C++<pre><code>##include &lt;boost/asio.hpp&gt;
##include &lt;iostream&gt;

using namespace boost::asio;

int main(int argc, char* argv[]){
    // 1. 创建 io_context 上下文
    io_context io;
    // 创建端点
    ip::tcp::endpoint ep(ip::address::from_string(argv[1]), std::atoi(argv[2]));
    // 2. 创建接受器, 并且与 io 关联
    ip::tcp::acceptor acceptor(io, ep);

    while(true){
        // 3. 创建客户端连接用的 socket
        ip::tcp::socket socket(io);
        // 4. 等待客户端连接
        acceptor.accept(socket);

        while(true){
            char buf[512];
            memset(buf, '\0', sizeof buf);

            // 5. 接收数据
            socket.read_some(buffer(buf));
            std::cout &lt;&lt; buf &lt;&lt; std::endl;
            // 6. 发送数据
            std::cin.getline(buf, 512);
            socket.write_some(buffer(buf));
        }
    }

    return 0;
}
</code></pre>

C++<pre><code>##include &lt;boost/asio.hpp&gt;
##include &lt;iostream&gt;

using namespace boost::asio;

int main(int argc, char* argv[]){
    // 1. 创建 io_context 上下文
    io_context io;
    // 2. 创建 socket, 并且与 io 关联
    ip::tcp::socket socket(io);
    // 创建端点
    ip::tcp::endpoint ep(ip::address::from_string(argv[1]), std::atoi(argv[2]));
    // 3. 连接服务器
    socket.connect(ep);

    while(true){
        char buf[512];
        memset(buf, '\0', sizeof buf);

        // 4. 发送数据
        std::cin.getline(buf, 512);
        socket.write_some(buffer(buf));
        // 5. 接收数据
        socket.read_some(buffer(buf));
        std::cout &lt;&lt; buf &lt;&lt; std::endl;
    }

    return 0;
}
</code></pre>

JwtUtils.java Java<pre><code>// JwtUtils.java
package org.dxl.utils;

import io.jsonwebtoken.Claims;
import io.jsonwebtoken.Jws;
import io.jsonwebtoken.JwsHeader;
import io.jsonwebtoken.Jwts;
import io.jsonwebtoken.security.Keys;
import io.jsonwebtoken.security.SecureDigestAlgorithm;

import javax.crypto.SecretKey;
import java.time.Instant;
import java.util.Date;
import java.util.Map;
import java.util.UUID;

public class JwtUtils {
    private static final String SECRET = "12345678901234567890123456789012"; // 32个字节的密钥
    private static final Integer expireTime = 12 * 60 * 60; // token有效期
    private static final String ISSUER = "dxl"; // Token 签发者
    private static final SecureDigestAlgorithm&lt;SecretKey, SecretKey&gt; ALGORITHM = Jwts.SIG.HS256; // 签名算法

    //subject: token 使用主体，通常是token所有人的ID
    // username 和 userid 用于 payload
    public static String buildJwt(String subject, Map&lt;String, Object&gt; claims){
        String uuid = UUID.randomUUID().toString();
        Date expireDate = Date.from(Instant.now().plusSeconds(expireTime));
        //HMAC : Hash-based Message Authentication Code
        SecretKey KEY = Keys.hmacShaKeyFor(SECRET.getBytes()); // 基于密钥产生 SecretKey
        return Jwts.builder()
                .header()
                //设置头部信息header
                .add("typ", "JWT")
                .add("alg", "HS256")
                .and()
                //设置自定义负载信息payload
                .claims(claims)
                //令牌ID
                .id(uuid)
                // 过期时间
                .expiration(expireDate)
                //生效时间
                .notBefore(new Date())
                //签发时间
                .issuedAt(new Date())
                //令牌所有者，通常是用户ID
                .subject(subject)
                //签发者
                .issuer(ISSUER)
                //签名
                .signWith(KEY, ALGORITHM)
                .compact();
    }

    public static Jws&lt;Claims&gt; parseToken(String token) {
        SecretKey KEY = Keys.hmacShaKeyFor(SECRET.getBytes());
        return Jwts.parser()
                .verifyWith(KEY)
                .build()
                .parseSignedClaims(token);
    }

    public static JwsHeader parseHeader(String token) {
        return parseToken(token).getHeader();
    }

    public static Claims parsePayload(String token) {
        return parseToken(token).getPayload();
    }

    public static String getSubject(String token) {
        return parseToken(token).getPayload().getSubject();
    }

    public static boolean validateToken(String token) {
        try {
            parseToken(token);
            return true;
        } catch (Exception e) {
            return false;
        }
    }
}

/*
&lt;dependency&gt;
    &lt;groupId&gt;io.jsonwebtoken&lt;/groupId&gt;
    &lt;artifactId&gt;jjwt&lt;/artifactId&gt;
    &lt;version&gt;0.12.5&lt;/version&gt;
&lt;/dependency&gt;
*/
</code></pre>

LoginCheckInterceptor.java Java<pre><code>// LoginCheckInterceptor.java
package org.dxl.interceptor;

import jakarta.servlet.http.HttpServletRequest;
import jakarta.servlet.http.HttpServletResponse;
import lombok.extern.slf4j.Slf4j;
import org.springframework.stereotype.Component;
import org.springframework.util.StringUtils;
import org.springframework.web.servlet.HandlerInterceptor;
import org.springframework.web.servlet.ModelAndView;
import org.dxl.utils.JwtUtils;

@Slf4j
@Component
public class LoginCheckInterceptor implements HandlerInterceptor {
    @Override
    // 目标资源方法运行前执行，返回 true: 执行；返回 false: 不执行
    public boolean preHandle(HttpServletRequest request, HttpServletResponse response, Object handler) throws Exception {
        //1. 获取请求头中的令牌
        String jwt = request.getHeader("token");
        //2. 判断令牌是否存在
        if (!StringUtils.hasLength(jwt)){
            log.info("请求头token为空，返回未登录的信息");
            return false;
        }
        //3. 解析token,如果解析失败，返回 false
        try{
            JwtUtils.parseToken(jwt);
        }catch (Exception e){ // 解析失败
            log.info("token解析失败");
            return false;
        }
        //解析token成功
        return HandlerInterceptor.super.preHandle(request, response, handler);
    }

    @Override
    // 目标资源方法运行后执行
    public void postHandle(HttpServletRequest request, HttpServletResponse response, Object handler, ModelAndView modelAndView) throws Exception {
        HandlerInterceptor.super.postHandle(request, response, handler, modelAndView);
    }

    @Override
    // 视图渲染完毕后执行，最后运行，执行释放资源的工作
    public void afterCompletion(HttpServletRequest request, HttpServletResponse response, Object handler, Exception ex) throws Exception {
        HandlerInterceptor.super.afterCompletion(request, response, handler, ex);
    }
}
</code></pre>

WebConfig.java Java<pre><code>// WebConfig.java
package org.dxl.config;

import jakarta.annotation.Resource;
import org.springframework.context.annotation.Configuration;

import org.springframework.web.servlet.config.annotation.InterceptorRegistry;
import org.springframework.web.servlet.config.annotation.WebMvcConfigurer;
import org.dxl.interceptor.LoginCheckInterceptor;

@Configuration
public class WebConfig implements WebMvcConfigurer {
    @Resource
    private LoginCheckInterceptor loginCheckInterceptor;

    @Override
    public void addInterceptors(InterceptorRegistry registry) {
        registry.addInterceptor(loginCheckInterceptor).addPathPatterns("/**").excludePathPatterns("/login");
        WebMvcConfigurer.super.addInterceptors(registry);
    }
}
</code></pre>

